  v_Processed VARCHAR(60) ;
  v_C_Remittance_ID VARCHAR2(32) ;
  v_Prec NUMBER:=2;
  v_Currency        VARCHAR2(32);

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;


  IF INSERTING THEN
    v_C_Remittance_ID:=:NEW.C_Remittance_ID;
  ELSE
    v_C_Remittance_ID:=:OLD.C_Remittance_ID;
  END IF;
  SELECT PROCESSED
  INTO v_Processed
  FROM C_Remittance
  WHERE C_Remittance_ID=v_C_Remittance_ID;
  IF UPDATING THEN
    IF(v_Processed='Y' AND
         (
       (COALESCE(:old.ISACTIVE, '')!=COALESCE(:NEW.ISACTIVE, ''))
       OR(COALESCE(:old.LINE, 0)!=COALESCE(:NEW.LINE, 0))
       OR(COALESCE(:old.C_DEBT_PAYMENT_ID, '0')!=COALESCE(:NEW.C_DEBT_PAYMENT_ID, '0'))
       OR(COALESCE(:old.C_DEBT_PAYMENT_CANCELLED, '0')!=COALESCE(:NEW.C_DEBT_PAYMENT_CANCELLED, '0'))
       OR(COALESCE(:old.AD_ORG_ID, '0') <> COALESCE(:new.AD_ORG_ID, '0'))
       OR(COALESCE(:old.AD_CLIENT_ID, '0') <> COALESCE(:new.AD_CLIENT_ID, '0'))
       )) THEN
      RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
    END IF;
  END IF;
  IF((DELETING OR INSERTING) AND v_Processed='Y') THEN
    RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ;
  END IF;
END C_REMITTANCELINE_TRG
