v_DateNull DATE := TO_DATE('01-01-1900', 'DD-MM-YYYY');
BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF UPDATING THEN IF(:old.Processed='Y' AND ((COALESCE(:old.C_BPartner_ID, '0') <> COALESCE(:new.C_BPartner_ID, '0')) OR(COALESCE(:old.DOCUMENTNO, '.') <> COALESCE(:new.DOCUMENTNO, '.')) OR(COALESCE(:old.C_DOCTYPE_ID, '0') <> COALESCE(:new.C_DOCTYPE_ID, '0')) OR(COALESCE(:old.AD_USER_ID, '0') <> COALESCE(:new.AD_USER_ID, '0')) OR(COALESCE(:old.C_ORDER_ID, '0') <> COALESCE(:new.C_ORDER_ID, '0')) OR(COALESCE(:old.DATEORDERED, v_DateNull) <> COALESCE(:new.DATEORDERED, v_DateNull)) OR(COALESCE(:old.MOVEMENTDATE, v_DateNull) <> COALESCE(:new.MOVEMENTDATE, v_DateNull)) OR(COALESCE(:OLD.C_BPARTNER_LOCATION_ID, '0') <> COALESCE(:NEW.C_BPARTNER_LOCATION_ID, '0')) OR(COALESCE(:old.PICKDATE, v_DateNull) <> COALESCE(:new.PICKDATE, v_DateNull)) OR(COALESCE(:old.SHIPDATE, v_DateNull) <> COALESCE(:new.SHIPDATE, v_DateNull)) OR(COALESCE(:old.C_CHARGE_ID, '0') <> COALESCE(:new.C_CHARGE_ID, '0')) OR(COALESCE(:old.CHARGEAMT, 0) <> COALESCE(:new.CHARGEAMT, 0)) OR(COALESCE(:old.AD_ORGTRX_ID, '0') <> COALESCE(:new.AD_ORGTRX_ID, '0')) OR(COALESCE(:old.USER1_ID, '0') <> COALESCE(:new.USER1_ID, '0')) OR(COALESCE(:old.M_SHIPPER_ID, '0') <> COALESCE(:new.M_SHIPPER_ID, '0')) OR(COALESCE(:old.SALESREP_ID, '0') <> COALESCE(:new.SALESREP_ID, '0')) OR(COALESCE(:old.M_WAREHOUSE_ID, '0') <> COALESCE(:new.M_WAREHOUSE_ID, '0')) OR(COALESCE(:old.USER2_ID, '0') <> COALESCE(:new.USER2_ID, '0')) OR(COALESCE(:old.DELIVERYRULE, '.') <> COALESCE(:new.DELIVERYRULE, '.')) OR(COALESCE(:old.DELIVERYVIARULE, '.') <> COALESCE(:new.DELIVERYVIARULE, '.')) OR(COALESCE(:old.PRIORITYRULE, '.') <> COALESCE(:new.PRIORITYRULE, '.')) OR(COALESCE(:old.AD_ORG_ID, '0') <> COALESCE(:new.AD_ORG_ID, '0')) OR(COALESCE(:old.AD_CLIENT_ID, '0') <> COALESCE(:new.AD_CLIENT_ID, '0')))) THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; IF(:old.Posted='Y' AND ((COALESCE(:old.DATEACCT, v_DateNull) <> COALESCE(:new.DATEACCT, v_DateNull)) OR (COALESCE(:old.MOVEMENTDATE, v_DateNull) <> COALESCE(:new.MOVEMENTDATE, v_DateNull)) OR(COALESCE(:old.C_CAMPAIGN_ID, '0') <> COALESCE(:new.C_CAMPAIGN_ID, '0')) OR(COALESCE(:old.C_PROJECT_ID, '0') <> COALESCE(:new.C_PROJECT_ID, '0')) OR(COALESCE(:old.C_ACTIVITY_ID, '0') <> COALESCE(:new.C_ACTIVITY_ID, '0')))) THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; IF(DELETING) THEN IF(:old.Processed='Y') THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; IF(INSERTING) THEN IF(:NEW.Processed='Y') THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; END M_INOUT_CHK_RESTRICTIONS_TRG
