BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF DELETING THEN IF (:old.PROCESSED = 'Y') THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; IF UPDATING THEN IF (:old.PROCESSED = 'Y' AND( (:new.PROCESSED = 'N') OR (COALESCE(:old.MOVEMENTDATE, v_DateNull) <> COALESCE(:new.MOVEMENTDATE, v_DateNull)) OR (COALESCE(:old.AD_ORGTRX_ID, '0') <> COALESCE(:new.AD_ORGTRX_ID, '0')) OR (COALESCE(:old.M_WAREHOUSE_ID, '0') <> COALESCE(:new.M_WAREHOUSE_ID, '0')) OR(COALESCE(:old.AD_ORG_ID, '0') <> COALESCE(:new.AD_ORG_ID, '0')) OR(COALESCE(:old.AD_CLIENT_ID, '0') <> COALESCE(:new.AD_CLIENT_ID, '0')))) THEN RAISE_APPLICATION_ERROR(-20501, 'Document processed/posted') ; END IF; END IF; END M_INVENTORY_TRG
